import express from 'express';
import { addUserRating, getUserCourseProgress, getUserData, purchaseCourse, updateUserCouresProgress, userEnrolledCourses } from '../controllers/userController.js';
import { addComment } from '../controllers/commentController.js';
const userRouter = express.Router();
userRouter.get('/data', getUserData);
userRouter.get('/enrolled-courses', userEnrolledCourses);
userRouter.post('/purchase', purchaseCourse);
userRouter.post('/update-course-progress', updateUserCouresProgress);
userRouter.post('/get-course-progress', getUserCourseProgress);
userRouter.post('/add-rating', addUserRating);
userRouter.post('/add-comment', addComment);
export default userRouter;
